        }
    }
    public struct NumberPairPatternListInput
    {
        public int k { get; set; }
        public int d { get; set; }
        public List<string> patterns { get; set; }

        public NumberPairPatternListInput(int _k, int _d, List<string> _patterns)
        {
            k = _k;
            d = _d;
            patterns = _patterns;
        }
        public override string ToString()
        {
            var message = String.Format("(k,d)=({0},{1})\n", k, d);
            foreach (var pattern in patterns)
            {
                message += string.Format("{0}\n", pattern);
            }
            return message;
        }
    }
