        public int k { get; set; }
        public int d { get; set; }
        public List<(string, string)> pairs { get; set; }

        public NumberPairPatternListCompositionsInput(int _k, int _d, List<(string, string)> _pairs)
        {
            k = _k;
            d = _d;
            pairs = _pairs;
        }
        public override string ToString()
        {
            var message = String.Format("(k,d)=({0},{1})\n", k, d);
            foreach (var pair in pairs)
            {
                message += string.Format("{0} | {1}\n", pair.Item1, pair.Item2);
            }
            return message;
        }
    }
    public struct TextPairInput
    {
