        }
    }

    public struct TextListNumberListNumberInput
    {
        public List<string> patterns { get; set; }
        public List<int> numbers { get; set; }
        public int k { get; set; }
        public TextListNumberListNumberInput(List<string> _patterns, List<int> _numbers, int _k)
        {
            patterns = _patterns;
            numbers = _numbers;
            k = _k;
        }

        public override string ToString()
        {
            var message = String.Format("Patterns:\n");
            foreach (var pattern in patterns)
            {
                message += string.Format("{0} ", pattern);
            }
            message += String.Format("\nNumbers:\n");
            foreach (var number in numbers)
            {
                message += string.Format("{0} ", number);
            }
            message += String.Format("\nk: {0}", k);
            return message;
        }
    }
